mapper.select:
SELECT count(id) FROM core_role_permission WHERE permission_id in
#{permissionId,jdbcType=BIGINT}
AND role_id = #{roleId,jdbcType=BIGINT} AND isactive = 1

mapper.delete:
DELETE from core_role_permission where permission_id in
#{permissionId,jdbcType=BIGINT}
AND role_id = #{roleId,jdbcType=BIGINT} AND isactive = 1
